   ```javascript
   // src/js/fhir-client.js
   const getAllergyData = async (client, patientId) => {
     try {
       // Query for AllergyIntolerance resources
       const allergies = await client.request(`AllergyIntolerance?patient=${patientId}`, {
         resolveReferences: ['patient', 'asserter'],
         pageLimit: 0,
         flat: true
       });

       console.log('Retrieved allergy data:', allergies);
       return allergies;
     } catch (error) {
       console.error('Error retrieving allergy data:', error);
       throw error;
     }
   };
   ```
