   // src/js/json-display.js
   const displayJsonData = (data, targetElementId) => {
     const targetElement = document.getElementById(targetElementId);
     if (!targetElement) {
       console.error(`Target element ${targetElementId} not found`);
       return;
     }

     // Format JSON with indentation for readability
     const formattedJson = JSON.stringify(data, null, 2);

     // Create pre element for formatted display
     const preElement = document.createElement('pre');
     preElement.className = 'json-display';
     preElement.textContent = formattedJson;

     // Clear previous content and append new JSON
     targetElement.innerHTML = '';
     targetElement.appendChild(preElement);
   };
   ```
