const createTestUI = () => {
  const testPanel = document.createElement('div');
  testPanel.id = 'test-panel';
  testPanel.className = 'test-panel';

  const header = document.createElement('h2');
  header.textContent = 'Test Panel';
  testPanel.appendChild(header);

  const runButton = document.createElement('button');
  runButton.textContent = 'Run Tests';
  runButton.onclick = async () => {
    const resultsElement = document.getElementById('test-results');
    resultsElement.innerHTML = '<p>Running tests...</p>';
    const results = await runTests();
    displayTestResults(results, resultsElement);
  };
  testPanel.appendChild(runButton);

  const resultsElement = document.createElement('div');
  resultsElement.id = 'test-results';
  testPanel.appendChild(resultsElement);

  document.body.appendChild(testPanel);
};

// Function to display test results in the UI
const displayTestResults = (results, container) => {
  container.innerHTML = '';

  const createResultElement = (name, result) => {
    const element = document.createElement('div');
    element.className = `test-result ${result.pass ? 'pass' : 'fail'}`;
    element.innerHTML = `
      <h4>${name}</h4>
      <p>${result.message}</p>
      ${result.error ? `<pre>${result.error.toString()}</pre>` : ''}
    `;
    return element;
  };

  for (const [category, tests] of Object.entries(results)) {
    const categoryElement = document.createElement('div');
    categoryElement.className = 'test-category';

    const categoryHeader = document.createElement('h3');
    categoryHeader.textContent = category;
    categoryElement.appendChild(categoryHeader);

    for (const [name, result] of Object.entries(tests)) {
      categoryElement.appendChild(createResultElement(name, result));
    }

    container.appendChild(categoryElement);
  }
};
```

