```javascript
// src/js/browser-adaptations.js
const applyBrowserAdaptations = () => {
  const { name, version } = detectBrowser();
  console.log(`Detected browser: ${name} ${version}`);

  // Add a class to the body for CSS targeting
  document.body.classList.add(`browser-${name}`);

  // Safari-specific adaptations
  if (name === 'safari') {
    // Fix for Safari-specific date handling issues
    window.formatDate = (dateString) => {
      if (!dateString) return '';
      const date = new Date(dateString);
      // Safari needs special date formatting
      return date.toLocaleDateString('en-US', { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric' 
      });
    };
  }

  // Firefox-specific adaptations
  if (name === 'firefox') {
    // Fix for Firefox-specific flexbox issues
    const allergyList = document.getElementById('allergy-list');
    if (allergyList) {
      allergyList.style.display = 'block';
      const cards = allergyList.querySelectorAll('.allergy-card');
      cards.forEach(card => {
        card.style.marginBottom = '10px';
      });
    }
  }

  // IE and older Edge adaptations
  if (name === 'ie' || (name === 'edge' && parseInt(version) < 79)) {
    // Add warning for unsupported browsers
    const warning = document.createElement('div');
    warning.className = 'browser-warning';
    warning.textContent = 'You are using an unsupported browser. Some features may not work correctly.';
    document.body.insertBefore(warning, document.body.firstChild);
  }
};
```

